import{_ as s,o as a,c as l,O as n}from"./chunks/framework.cc2ec653.js";const u=JSON.parse('{"title":"免费开源的投屏软件","description":"手机项目验收，展示的时候，使用手机投屏软件成为了一个尴尬的点了。","frontmatter":{"date":"2022-11-12T00:00:00.000Z","title":"免费开源的投屏软件","tags":["投屏"],"description":"手机项目验收，展示的时候，使用手机投屏软件成为了一个尴尬的点了。"},"headers":[],"relativePath":"posts/0005.md"}'),e={name:"posts/0005.md"},t=n(`<h1 id="免费开源的投屏软件" tabindex="-1">免费开源的投屏软件 <a class="header-anchor" href="#免费开源的投屏软件" aria-label="Permalink to &quot;免费开源的投屏软件&quot;">​</a></h1><p>scrcpy 是一个开源免费投屏软件，安卓投屏神器。</p><p>使用前提</p><ul><li><strong>安卓</strong></li><li><strong>手机开启了开发者调试</strong></li></ul><h2 id="使用方法" tabindex="-1">使用方法 <a class="header-anchor" href="#使用方法" aria-label="Permalink to &quot;使用方法&quot;">​</a></h2><p>安装或下载 scrcpy</p><ul><li>linux <code>apt install scrcpy</code></li><li>macOS <code>brew install scrcpy</code></li><li>windows <ul><li>下载 <a href="https://github.com/Genymobile/scrcpy/releases/download/v1.24/scrcpy-win64-v1.24.zip" target="_blank" rel="noreferrer">https://github.com/Genymobile/scrcpy/releases/download/v1.24/scrcpy-win64-v1.24.zip</a></li><li><code>choco install scrcpy choco install adb</code></li><li><code>scoop install scrcpy scoop install adb </code></li></ul></li></ul><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># !!! 手机开启了开发者调试（设置 版本号 连按7次）</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># windows 下载为例</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 解压下载的文件 cd 解压目录</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 手机有线连接</span></span>
<span class="line"><span style="color:#82AAFF;">.</span><span style="color:#FFCB6B;">/adb</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">tcpip</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">5555</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 无线连接 ip 为手机ip 手机和电脑需要在同一局域网</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 手机ip 可通过查看无线详情</span></span>
<span class="line"><span style="color:#82AAFF;">.</span><span style="color:#FFCB6B;">/adb</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">connect</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ip:</span><span style="color:#F78C6C;">5555</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 连接成功后 直接打开 scrcpy.exe 即可</span></span></code></pre></div><h2 id="最后" tabindex="-1">最后 <a class="header-anchor" href="#最后" aria-label="Permalink to &quot;最后&quot;">​</a></h2><ul><li>关于更多的使用方法可查看 scrcpy github 页面</li><li>地址：<a href="https://github.com/Genymobile/scrcpy" target="_blank" rel="noreferrer">https://github.com/Genymobile/scrcpy</a></li></ul>`,10),o=[t];function c(p,i,r,d,y,h){return a(),l("div",null,o)}const b=s(e,[["render",c]]);export{u as __pageData,b as default};
